import { PetsContext } from './Contexts/PetsContext';
import {useState} from 'react';
import AddPetForm from './Components/AddPetForm/AddPetForm'
import Layout from "./Components/Layout/Layout.js";
import Home from './Components/Home/Home';
import {
  createBrowserRouter,
  RouterProvider,
} from "react-router-dom";

function App() {

  var startingPets = getPetTable();
  const[pets, setPets] = useState(startingPets);


  const router = createBrowserRouter([
    {
      path: "/",
      element: <Layout />,
      children: [
              {
                  path: "/viewpets",
                  element: <Home/>,
              },
              {
                  path: "addpet",
                  element: <AddPetForm />
              }
      ]
    }
  ]);

  {/*  export default handleAddPet(name, age, species){
    var petIds = pets.map(pet => pet.id);
    var highestId = Math.max(...petIds);

    var pet = {
      id: highestId +1,
      name: name,
      age: age,
      species: species
    }

    setPets([...pets, pet])
  } */}

  return (
    <div className="App">
      <PetsContext.Provider value={{pets, setPets}}>
          <RouterProvider router={router} />
      </PetsContext.Provider>
    </div>
  );

  function getPetTable(){

    let petArray = [
      {
        id: 1,
        name: "rex",
        age: 3,
        species: "labrapoodle",
      },
      {
        id: 2,
        name: "Porkchop",
        age: 12,
        species: "pig",
      }
    ]

    return(petArray);
  }

}

export default App;
